class Solution(object):
    def canCross(self, stones):
        """
        :type stones: List[int]
        :rtype: bool
        """
        last_steps = {stone: set() for stone in stones}
        bumps = (-1, 0, 1)
        last_steps[0].add(0)
        for stone in stones:
            for last_step in last_steps[stone]:
                for bump in bumps:
                    next_stone = stone + last_step + bump
                    if next_stone > stone and next_stone in last_steps:
                        last_steps[next_stone].add(next_stone - stone)
        return True if last_steps[stones[-1]] else False


